import React from 'react';
import PropTypes from 'prop-types';

const IngredientDish = ({ ingredient }) => (
  <p>{ingredient}</p>
);

const IngredientSteps = ({ step }) => (
  <p>{step}</p>
);

class DishPage extends React.PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      pic: '',
      ingredients: '',
      steps: '',
      time: '',
    };
  }

  render() {
    const {
      name, pic, ingredients, steps, time,
    } = this.state;
    return (
      <div className="page">
        <div className="page-content">
          <div className="row center">
            <h1>{name}</h1>
            <h2>{time}</h2>
          </div>
          <img src={pic} alt={pic} />
          { ingredients.map(item => (
            <IngredientDish ingredient={item} />
          )) }
          { steps.map(item => (
            <IngredientSteps step={item} />
          )) }
        </div>
      </div>
    );
  }

}

IngredientSteps.propTypes = {
  step: PropTypes.string.isRequired,
};

IngredientDish.propTypes = {
  ingredient: PropTypes.string.isRequired,
};

DishPage.propTypes = {
  name: PropTypes.string.isRequired,
  pic: PropTypes.string.isRequired,
  ingredients: PropTypes.string.isRequired,
  steps: PropTypes.string.isRequired,
  time: PropTypes.string.isRequired,
};

export default DishPage;
