import React from 'react';
import PropTypes from 'prop-types';
import { getBDDPlat } from '../../APIManager';

const IngredientDish = ({ name, pic }) => (
  <p>{name} {pic}</p>
);

const IngredientSteps = ({ step }) => (
  <p>{step}</p>
);

class DishPage extends React.PureComponent {
  static propTypes = {
    match: PropTypes.shape({
      params: PropTypes.shape({
        name: PropTypes.string.isRequired,
      }),
    }).isRequired,
  };

  constructor(props) {
    super(props);
    const { match } = this.props;
    const { name } = match.params;
    this.state = {
      name: name.platName,
      pic: '',
      ingredients: [],
      steps: [],
      time: '',
      error: '',
    };
    this.getPlat(name);
  }

  getPlat = (name) => {
    getBDDPlat(name)
      .then((item) => {
        this.setState({
          ...item,
        });
      })
      .catch((error) => {
        this.setState({ error });
      });
  }

  render() {
    const {
      name, pic, ingredients, steps, time, error,
    } = this.state;
    return (
      <div className="page">
        <div className="page-content">
          <div className="row center">
            <h1>{name}</h1>
            <h2>{time}</h2>
          </div>
          { error && <p>{error}</p>}
          <img src={pic} alt={pic} />
          { ingredients.map(item => (
            <IngredientDish {...item} />
          )) }
          { steps.map(item => (
            <IngredientSteps step={item} />
          )) }
        </div>
      </div>
    );
  }
}

IngredientSteps.propTypes = {
  step: PropTypes.string.isRequired,
};

IngredientDish.propTypes = {
  ingredient: PropTypes.string.isRequired,
};

export default DishPage;
