import React from 'react';
import PropTypes from 'prop-types';
import { getBDDPlat } from '../../APIManager';
import ButtonSwag from '../../components/ButtonSwag/ButtonSwag';
import './DishPage.css';

const IngredientDish = ({ name, pic }) => (
  <p key={name}>
    {name}
    {pic}
  </p>
);

const IngredientSteps = ({ step }) => (
  <p key={step}>{step}</p>
);

class DishPage extends React.PureComponent {
  static propTypes = {
    match: PropTypes.shape({
      params: PropTypes.shape({
        name: PropTypes.string.isRequired,
      }),
    }).isRequired,
  };

  constructor(props) {
    super(props);
    const { match } = this.props;
    const { name } = match.params;
    this.state = {
      name,
      pic: '',
      ingredients: [],
      steps: [],
      time: '',
      error: '',
    };
    this.getPlat(name);
  }

  getPlat = (name) => {
    getBDDPlat(name)
      .then((item) => {
        this.setState({
          ...item,
        });
      })
      .catch((error) => {
        this.setState({ error });
      });
  }

  render() {
    const {
      name, pic, ingredients, steps, time, error,
    } = this.state;
    const { history } = this.props;
    return (
      <div className="dish">
        <section className="row center dish-top">
          <ButtonSwag text="Back to ingredients" style={{ marginLeft: '3%' }} onClick={() => history.push('ingredients')} />
          <h1>{name}</h1>
          <h2>{time}</h2>
        </section>
        { error && <p>{error}</p>}
        <section className="dish-middle">
          <img className="image" src={pic} alt={pic} />
        </section>
        <section className="dish-bottom">
          <article className="dish-ingredientsList">
            { ingredients.map(item => (
              <ul>
                <li><img src={item.pic} alt={item.name} /></li>
                <li>{item.name}</li>
              </ul>
            ))}
          </article>
          <article className="dish-steps">
            { steps.map(item => (
              <IngredientSteps step={item} />
            )) }
          </article>
        </section>
      </div>
    );
  }
}

IngredientSteps.propTypes = {
  step: PropTypes.string.isRequired,
};

IngredientDish.propTypes = {
  name: PropTypes.string.isRequired,
  pic: PropTypes.string.isRequired,
};

DishPage.propTypes = {
  history: PropTypes.shape({
    length: PropTypes.number,
  }).isRequired,
}

export default DishPage;
