import React from 'react';
import PropTypes from 'prop-types';
import { getBDDPlat } from '../../APIManager';

const IngredientDish = ({ ingredient }) => (
  <p>{ingredient}</p>
);

const IngredientSteps = ({ step }) => (
  <p>{step}</p>
);

class DishPage extends React.PureComponent {
  constructor(props) {
    super(props);
    const { name } = props;
    this.state = {
      name: { name },
      pic: '',
      ingredients: '',
      steps: '',
      time: '',
      error: '',
    };
  }

  getPlat = (name) => {
    getBDDPlat(name)
      .then((item) => {
        this.setState({
          ...item,
        });
      })
      .catch((error) => {
        this.setState({ error });
      });
  }

  render() {
    const {
      name, pic, ingredients, steps, time, error,
    } = this.state;
    return (
      <div className="page">
        <div className="page-content">
          <div className="row center">
            <h1>{name}</h1>
            <h2>{time}</h2>
          </div>
          { error && <p>{error}</p>}
          <img src={pic} alt={pic} />
          { ingredients.map(item => (
            <IngredientDish ingredient={item} />
          )) }
          { steps.map(item => (
            <IngredientSteps step={item} />
          )) }
        </div>
      </div>
    );
  }
}

IngredientSteps.propTypes = {
  step: PropTypes.string.isRequired,
};

IngredientDish.propTypes = {
  ingredient: PropTypes.string.isRequired,
};

DishPage.propTypes = {
  name: PropTypes.string.isRequired,
};

export default DishPage;
